mapper.update:
UPDATE tbl_reply
SET

    title = #{title, jdbcType=VARCHAR},


    content = #{content, jdbcType=VARCHAR}

WHERE inquiry_id = #{inquiryId, jdbcType=NUMERIC}
AND inquiry_writer_email = #{inquiryWriterEmail, jdbcType=VARCHAR}

UPDATE tbl_reply
SET delete_flag = FALSE
WHERE inquiry_id = #{inquiryId, jdbcType=NUMERIC}
AND inquiry_writer_email = #{inquiryWriterEmail, jdbcType=VARCHAR}
