mapper.select:
SELECT ROW_NUMBER() OVER (ORDER BY created_at) AS row_number,
       email,
       name,
       phone_number,
       user_division,
       TO_CHAR(created_at, 'YYYY-MM-DD') AS created_date,
       TO_CHAR(updated_at, 'YYYY-MM-DD') AS updated_date
FROM tbl_user



        email LIKE #{likeEmail, jdbcType=VARCHAR}



        name LIKE #{likeName, jdbcType=VARCHAR}


        user_division = #{userDivision, jdbcType=VARCHAR}


ORDER BY created_at DESC

    LIMIT #{limit}


    OFFSET #{offset}

SELECT COUNT(1)
FROM tbl_user



        email LIKE #{likeEmail, jdbcType=VARCHAR}



        name LIKE #{likeName, jdbcType=VARCHAR}


        user_division = #{userDivision, jdbcType=VARCHAR}

mapper.update:
UPDATE tbl_user
SET delete_flag = FALSE
WHERE email = #{email, jdbcType=NUMERIC}
